import { ResponseType } from "expo-auth-session";
import * as Facebook from "expo-auth-session/providers/facebook";
import * as WebBrowser from "expo-web-browser";
import React, {useState, useEffect} from "react";
import { Button, Image, StyleSheet, Text, View } from "react-native";
import { color } from "react-native-reanimated";
import Color from "../../../Config/Color";



WebBrowser.maybeCompleteAuthSession();

// const FB_APP_ID = "466814147702243";
// const FB_APP_ID = "145668956753819";

export default function FBAuth(props) {
  const [user, setUser] = useState(null);
  // Request
  const [request, response, promptAsync] = Facebook.useAuthRequest({
    clientId: process.env.FB_APP_ID,
    // NOTICE: Please do not actually request the token on the client (see:
    // response_type=token in the authUrl), it is not secure. Request a code
    // instead, and use this flow:
    // https://developers.facebook.com/docs/facebook-login/manually-build-a-login-flow/#confirm
    // The code here is simplified for the sake of demonstration. If you are
    // just prototyping then you don't need to concern yourself with this and
    // can copy this example, but be aware that this is not safe in production.
    responseType: ResponseType.Token,
  });

  if (request) {
    console.log(
      "You need to add this url to your authorized redirect urls on your Facebook app: " +
        request.redirectUri+JSON.stringify(request)
    );
  }

  useEffect(() => {
    if (response && response.type === "success" && response.authentication) {
      (async () => {
        const userInfoResponse = await fetch(
          `https://graph.facebook.com/me?access_token=${response.authentication.accessToken}&fields=id,name,picture.type(large)`
        );
        const userInfo = await userInfoResponse.json();
        setUser(userInfo);
      })();
    }
  }, [response]);
  

  const handlePressAsync = async () => {
    const result = await promptAsync();

    if (result.type !== "success") {
      alert("Uh oh, something went wrong");
      return;
    }
  };

  return (
    <View style={{ marginVertical: 10, marginHorizontal: 15 }}>
    {user ? (
        <>
        <Profile user={user} />
        <Button 
        title="logout"
        />
        </>
      ) : (
        <Button
          disabled={!request}
          title="Login with Facebook"
          textStyle={{ fontSize: 15, fontWeight: "100" }}
          viewStyle={{ backgroundColor: Color.facebook,borderRadius:70 }}
          onPress={handlePressAsync}
          //onPress={this.FBLogIn}
        />
        
      )}
    </View>
  );
}

function Profile({ user }) {
  return (
    
    <View style={styles.profile}>
      <Image source={{ uri: user.picture.data.url }} style={styles.image} />
      <Text style={styles.name}>{user.name}</Text>
      <Text>ID: {user.id}</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  profile: {
    alignItems: "center",
  },
  name: {
    fontSize: 20,
  },
  image: {
    width: 70,
    height: 70,
    borderRadius: 50,
  },
  social:{
    borderRadius:50,
    backgroundColor:Color.facebook
  }
});
